(ns seabass.test.utils	(:use [seabass.utils] :reload)	(:use [clojure.test]))	(deftest util-validity-test	(is (= true (valid-dt? "boolean" "true")))	(is (= true (valid-dt? "boolean" "false")))	(is (= true (valid-dt? "date" "2002-10-10")))	(is (= false (valid-dt? "date" "2002-14-15")))	(is (= true (valid-dt? "datetime" "2002-10-10T12:00:00-05:00")))	(is (= false (valid-dt? "datetime" "12.3")))	(is (= true (valid-dt? "decimal" "12.3")))	(is (= false (valid-dt? "decimal" "foo")))	(is (= true (valid-dt? "double" "-13.2")))	(is (= false (valid-dt? "double" "foo")))	(is (= true (valid-dt? "duration" "P1347Y")))	(is (= false (valid-dt? "duration" "P1Y2MT")))	(is (= true (valid-dt? "float" "-15.982")))	(is (= false (valid-dt? "float" "foo")))	(is (= true (valid-dt? "integer" "12")))	(is (= false (valid-dt? "integer" "12.0")))	(is (= true (valid-dt? "string" "foo")))	(is (= true (valid-dt? "string" "12")))	(is (= true (valid-dt? "time" "00:00:00")))	(is (= false (valid-dt? "time" "24:47:00"))))